import { useState, useEffect } from 'react'
import DisplayWeather from './DisplayWeather';
import "./weather.css"

function Weather() {
    /* */
    const [location, setLocation] = useState({
        loaded: false,
        coordinates: {
            lat: "",
            lng: ""
        }
    });
    const onSuccess = location => {
        setLocation({
            loaded: true,
            coordinates: {
                lat: location.coords.latitude,
                lng: location.coords.longitude
            }
        });
    }
    function codeLatLng(lat, lng) {

        var latlng = new google.maps.LatLng(lat, lng);
        geocoder.geocode({ 'latLng': latlng }, function (results, status) {
            if (status == google.maps.GeocoderStatus.OK) {
                console.log(results)
                if (results[1]) {
                    //formatted address
                    alert(results[0].formatted_address)
                    //find country name
                    for (var i = 0; i < results[0].address_components.length; i++) {
                        for (var b = 0; b < results[0].address_components[i].types.length; b++) {

                            //there are different types that might hold a city admin_area_lvl_1 usually does in come cases looking for sublocality type will be more appropriate
                            if (results[0].address_components[i].types[b] == "administrative_area_level_1") {
                                //this is the object you are looking for
                                city = results[0].address_components[i];
                                break;
                            }
                        }
                    }
                    //city data
                    alert(city.short_name + " " + city.long_name)


                } else {
                    alert("No results found");
                }
            } else {
                alert("Geocoder failed due to: " + status);
            }
        });
    }
    const onError = error => {
        setLocation({
            loaded: true,
            error
        });
    }
    useEffect(() => {
        if (!("geolocation" in navigator)) {
            onError({
                code: 0,
                message: "Geolocation not supported"
            });
        }
        navigator.geolocation.getCurrentPosition(onSuccess, onError);
    }, [])
    /* */
    const APIKEY = "04c8214c779f00c5abe7841b3f816209";
    const [form, setForm] = useState({
        city: "",
        country: "",
        coords: {
            lat: location.coordinates.lat,
            lon: location.coordinates.lng,
        }
    });
    const [weather, setWeather] = useState([]);
    async function weatherData(e) {
        e.preventDefault();
        if (form.city === "") {
            const data = await fetch(
                `https://api.openweathermap.org/data/2.5/forecast?lat=${location.coordinates.lat}&lon=${location.coordinates.lng}&appid=${APIKEY}`
            )
                .then((res) => res.json())
                .then(data => data);

            setWeather(
                {
                    data: data
                }
            );
        }
        else {
            const data = await fetch(
                `https://api.openweathermap.org/data/2.5/weather?q=${form.city},${form.country}&appid=${APIKEY}`
            )
                .then((res) => res.json())
                .then(data => data);

            setWeather(
                {
                    data: data
                }
            );
        }
    }
    const handleChange = (e) => {
        let name = e.target.name;
        let value = e.target.value;
        if (name === "city") {
            setForm({ ...form, city: value })
        }
        if (name === "country") {
            setForm({ ...form, country: value })
        }
        if (name === "lon") {
            setForm({ ...form, lon: value })
        }
        if (name === "lat") {
            setForm({ ...form, lat: value })
        }
    }
    return (
        <div className='weather'>
            <span className='title'>Weather App</span>
            <br />
            <form>
                <input type="text" name='city' placeholder='city' onChange={e => handleChange(e)} />
                <input type="text" name='country' placeholder='country' onChange={e => handleChange(e)} />
                <input type="text" name='lon' placeholder='lon' value={location.coordinates.lng} onChange={e => handleChange(e)} />
                <input type="text" name='lat' placeholder='lat' value={location.coordinates.lat} onChange={e => handleChange(e)} />
                <button className='getweather' onClick={(e) => weatherData(e)}>Get</button>
            </form>
            {
                weather.data !== undefined ?

                    <div>
                        <DisplayWeather data={weather.data}></DisplayWeather>
                    </div>

                    : null
            }
        </div>
    )
}

export default Weather
